//var dark_level = 100
var lights_on = flow.get("lights_on") || false

var msg_lamp_aan = { payload: '', topic: 'lights on' }
var msg_lamp_uit = { payload: '', topic: 'lights off' }

var dark_inside = msg.payload["schemer buiten"]
//var use_solar_detector = msg.payload["use solar detector"]
//var error_solarpanels = msg.payload.error
//var power_mean = msg.payload["sensor.thuis_total_output_power"]
var lampen_timer = msg.payload.Timer

function switch_lights() {
    // is it dark inside?
    if (lampen_timer == true && dark_inside == true) {
        on_change(true)
    }
    else {
        on_change(false)
    }
}

function on_change(lights) {

    var changed = lights != lights_on
    lights_on = lights
    if (changed) {
        if (lights_on) {
            msg_lamp_aan.payload = true
            msg_lamp_uit = null
        }
        else {
            msg_lamp_uit.payload = true
            msg_lamp_aan = null
        }
    }
    else {
        msg_lamp_aan = null
        msg_lamp_uit = null
    }
    return changed
}

switch_lights()
flow.set("lights_on", lights_on)
return [msg_lamp_aan, msg_lamp_uit]

