var donkerlevel = 0;
var schemerlevel = 0;
if(msg.topic === "buienradar"){schemerlevel = 500; donkerlevel = 150}
if(msg.topic === "solar power"){schemerlevel = 28; donkerlevel = 3}

var msg_donker = { topic: 'donker buiten', payload: false };
var msg_schemer = { topic: 'schemer buiten', payload: false };

if (msg.payload < donkerlevel) {
    msg_donker.payload = true;
} 
if (msg.payload < schemerlevel) {
    msg_schemer.payload = true;
}

return [msg_donker, msg_schemer];
